package org.zaza.Cpro;

public class Looping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0;
		do {
			i++;
			System.out.println(i);
		}
		while(i < 5);

		int j = 0;
		System.out.println("Dengan loop while ");
		while(j < 10) {
			System.out.println(j);
			j++;
		}
	}

}
